local PANEL = {}function PANEL:Init ()endfunction PANEL:Paint ()	if not self.Depressed then		draw.RoundedBox (4, 0, 0, self:GetWide (), self:GetTall (), Color (160, 160, 160, 255))	end		if self:GetDisabled () then		draw.RoundedBox (4, 1, 1, self:GetWide () - 2, self:GetTall () - 2, Color (100, 100, 100, 255))	elseif self.Depressed then		draw.RoundedBox (4, 0, 0, self:GetWide (), self:GetTall (), Color (144, 144, 160, 255))		draw.RoundedBox (4, 1, 1, self:GetWide () - 2, self:GetTall () - 2, Color (128, 128, 160, 255))	elseif self.Hovered then		draw.RoundedBox (4, 1, 1, self:GetWide () - 2, self:GetTall () - 2, Color (128, 128, 128, 255))	else		draw.RoundedBox (4, 1, 1, self:GetWide () - 2, self:GetTall () - 2, Color (72, 72, 72, 255))	endendvgui.Register ("NNJGShopButton", PANEL, "DButton")