local PANEL = {}local enabledTextColor        = Color (255, 255, 255, 255)local disabledTextColor       = Color (128, 128, 128, 255)local unpressedBorderColor    = Color (160, 160, 160, 255)local pressedBorderColor      = Color (144, 144, 160, 255)local defaultBackgroundColor  = Color ( 72,  72,  72, 255)local disabledBackgroundColor = Color (100, 100, 100, 255)local hoveredBackgroundColor  = Color (128, 128, 128, 255)local pressedBackgroundColor  = Color (128, 128, 160, 255)function PANEL:Init ()endfunction PANEL:Paint (w, h)	if not self.Depressed then		draw.RoundedBox (4, 0, 0, w, h, unpressedBorderColor)	end		self:SetTextColor (self:GetDisabled () and disabledTextColor or enabledTextColor)		if self:GetDisabled () then		draw.RoundedBox (4, 1, 1, w - 2, h - 2, disabledBackgroundColor)	elseif self.Depressed then		draw.RoundedBox (4, 0, 0, w,     h,     pressedBorderColor)		draw.RoundedBox (4, 1, 1, w - 2, h - 2, pressedBackgroundColor)	elseif self.Hovered then		draw.RoundedBox (4, 1, 1, w - 2, h - 2, hoveredBackgroundColor)	else		draw.RoundedBox (4, 1, 1, w - 2, h - 2, defaultBackgroundColor)	endendvgui.Register ("NNJGShopButton", PANEL, "DButton")